// Memoize
function memoize(fn) {
	const cache = {};

	// ... implies that you don't know how many arguments it will receive
	return function(...args) {
		if(cache[args]) {
			return cache[args];
		}

		const result = fn.apply(this, args);
		cache[args] = result;

		return result;
	}
}

